mkdir -p  ~/.ssh
echo -e "Host github.com\n\tStrictHostKeyChecking no\n\tUserKnownHostsFile /dev/null\n" >  ~/.ssh/config
echo -e "Host *\n\tStrictHostKeyChecking no\n" >> ~/.ssh/config
chmod 600  ~/.ssh
ssh-keyscan -Ht rsa github.com >>  ~/.ssh/known_hosts
cat  ~/.ssh/config

ssh-keyscan github.com > testhost.ssh-keyscan
cat testhost.ssh-keyscan
ssh-keygen -lf testhost.ssh-keyscan -E sha256
ssh -o FingerprintHash=sha256 github.com

set -e

# Workaround old docker images with incorrect $HOME
# check https://github.com/docker/docker/issues/2968 for details
if [ "${HOME}" = "/" ]
then
  export HOME=$(getent passwd $(id -un) | cut -d: -f6)
fi

mkdir -p ~/.ssh

echo '
github.com ssh-rsa 
bitbucket.org ssh-rsa 
' >> ~/.ssh/known_hosts
